record.field:
# –ü–æ–ª—É—á–∞–µ–º –≤—ã–±—Ä–∞–Ω–Ω—ã–µ attachment'—ã
attachment_ids = records.ids if records else []

if not attachment_ids:
    action = {
        'type': 'ir.actions.client',
        'tag': 'display_notification',
        'params': {
            'title': '–í–Ω–∏–º–∞–Ω–∏–µ!',
            'message': '–í—ã–±–µ—Ä–∏—Ç–µ –¥–æ–∫—É–º–µ–Ω—Ç –¥–ª—è –ø–æ–∏—Å–∫–∞ —Å–≤—è–∑–∞–Ω–Ω—ã—Ö –∑–∞—è–≤–æ–∫',
            'type': 'warning',
        }
    }
else:
    # –ò—Å–ø–æ–ª—å–∑—É–µ–º –ø–µ—Ä–≤—ã–π –≤—ã–±—Ä–∞–Ω–Ω—ã–π attachment
    attachment_id = attachment_ids[0]

    # –í—ã–∑—ã–≤–∞–µ–º –º–µ—Ç–æ–¥ –∏–∑ –º–æ–¥–µ–ª–∏ –∑–∞—è–≤–∫–∏
    zayavka_obj = env['amanat.zayavka']
    action = zayavka_obj.action_show_related_zayavkas_by_attachment(attachment_id)
